import React, { useState } from "react"
import * as serviceProduct from "../../service/ServiceProduct";
import {Field, Form, Formik} from "formik";

const ShopCart = ({ shopItems, addToCart }) => {
  const [count, setCount] = useState(0)
  const increment = () => {
    setCount(count + 1)
  }

  return (
    <>
      <Formik
          initialValues={{
            name: ''
          }}
          onSubmit={(values) => {
            const findName = async () => {
              const result = await serviceProduct.findByNameProduct(values.nameRacing)
              // await setSearch(values)
                addToCart(result.content)
            }
            findName()
          }}>{
        <Form className='search-box d_flex'>
          {/*<i className='fa fa-search'></i>*/}
          <Field type="text" name="name" placeholder='Nhập tên sản phẩm muốn tìm...'/>
          <button className="btn me-4" type="submit" style={{ width: '3rem' }}>
            <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor"
                 className="bi bi-search" viewBox="0 0 16 16">
              <path
                  d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z" />
            </svg>
          </button>
        </Form>}
      </Formik>
      {shopItems.map((shopItems, index) => {
        return (
          <div className='box'>
            <div className='product mtop'>
              <div className='img'>
                <span className='discount'>{shopItems.quantity}% Off</span>
                <img src={shopItems.images} alt='' height="500" width="500"/>
                <div className='product-like'>
                  <label>{count}</label> <br/>
                  <i className='fa-regular fa-heart' onClick={increment}></i>
                </div>
              </div>
              <div className='product-details'>
                <h3>{shopItems.nameRacing}</h3>
                <div className='rate'>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                </div>
                <div className='price'>
                  <h4>${shopItems.price} </h4>
                  <button onClick={() => addToCart(shopItems)}>
                    <i className='fa fa-plus'></i>
                  </button>
                </div>
              </div>
            </div>
          </div>
        )
      })}
    </>
  )
}

export default ShopCart
