import * as serviceProduct from "../../service/ServiceProduct";
import React, {useEffect, useState} from "react";
import "./style.css"
import {useParams} from "react-router";
import 'bootstrap/dist/css/bootstrap.min.css';
import {NavLink} from "react-router-dom";
import Slider from "react-slick";

export function DetailProduct() {
    const param = useParams()
    const [productDetail, setProductDetail] = useState([])
    const [product, setProduct] = useState([])
    const findDetailProduct = async () => {
        const result = await serviceProduct.detailProduct(param.id)
        setProductDetail(result)
    }
    const findAllListProduct = async () => {
        const result = await serviceProduct.findAllProductDetail()
        setProduct(result.content)
    }
    const settings = {
        dots: false,
        infinite: true,
        slidesToShow: 4,
        slidesToScroll: 2,
        autoplay: true,
    }
    useEffect(() => {
        findAllListProduct();
    }, [])
    useEffect(() => {
        findDetailProduct()
    }, [param.id])
    useEffect(() => {
        document.title = "Chi tiết sản phẩm "; // Thay đổi title
        window.scrollTo(0, 0)
    }, []);
    if (!productDetail) {
        return null
    }

    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-lg-8 col-xl-9 mb-5">
                        <div className="main-content mt-4">
                            <h1 style={{textAlign: "center", fontFamily: "Times New Roman"}}
                                className="title-news">{productDetail.nameRacing}</h1>
                            <div className="full-content mt-5"><p></p>
                                <div className="form-old-row" style={{display: "flex"}}>
                                    <img className="imgDetail" style={{
                                        width: "auto",
                                        display: "block",
                                        marginLeft: "auto",
                                        marginRight: "auto"
                                    }}
                                         src={productDetail.images} alt=""/>
                                </div>
                                <div
                                    style={{
                                        marginTop: "15px",
                                        fontSize: "15pt",
                                        color: "#666",
                                        fontFamily: "Times New Roman",
                                        // fontStyle: "italic",
                                        textAlign: "justify"
                                    }}>
                                    <p
                                        style={{
                                            lineHeight: "unset !important",
                                            margin: "3rem 4rem"
                                        }}>{productDetail.note}</p></div>
                            </div>
                        </div>
                    </div>
                    <div className="col-lg-4 col-xl-3">
                        <div className="right-pane">
                            <div className="main-news-other"><h2 className="title-other text-posts">Các sản phẩm
                                khác </h2>
                                <div className="list-news-other">{
                                    product.map((product) => (
                                        <div className="row mt-3">
                                            <div className="col-4">
                                                <div className="image-post"><a href={`/detailProduct/${product.id}`}>
                                                    <img style={{width: "100%", height: "100%", objectFit: "cover"}}
                                                         src={product.images} alt=""/> </a></div>
                                            </div>
                                            <div className="col-8 mt-2">
                                                <a className="text-decoration-none"
                                                   href={`/detailProduct/${product.id}`}><h3
